import React, { Component } from 'react';

class FormTask extends Component {
	changeField = (field, value) => {
		const { changeField } = this.props;
		const changes = {};
		changes[field] = value;
		changeField(changes);
	};

	addTask = () => {
		const {formTask, addTask} = this.props;
		addTask(formTask);
	};

	editTask = () => {
		const {formTask, sendEditTask} = this.props;
		sendEditTask(formTask);
	};

	render() {
		const { formTask } = this.props;
		return (
			<div>
				<input onChange={(e) => this.changeField('name', e.target.value)} value={formTask.name} type="text"/>
				<input onChange={(e) => this.changeField('description', e.target.value)} value={formTask.description} type="text"/>
				<input onChange={(e) => this.changeField('dateStart', e.target.value)} value={formTask.dateStart} type="date"/>
				<input onChange={(e) => this.changeField('dateEnd', e.target.value)} value={formTask.dateEnd} type="date"/>
				<button onClick={(formTask.type === 'create') ? this.addTask : this.editTask}>{(formTask.type === 'create') ? 'создать' : 'редактировать'}</button>
			</div>
		)
	}
}

export default FormTask;
