import React from 'react';
import uuid from 'uuid/v4';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class MembersForm extends React.PureComponent {
	addMembers = () => {
		const { members, updateMembers } = this.props;
		const editParticipants = (members) ? [...members] : [];
		if (this.input.value) {
			editParticipants.push({
				id: uuid(),
				name: this.input.value
			})
		}
		updateMembers(editParticipants);
		this.input.value = null
	};

	deleteMembers = (id) => {
		const { members, updateMembers } = this.props;
		const editParticipants = members.filter(members => members.id !== id);
		updateMembers(editParticipants);
	};

	render() {
		const { members } = this.props;
		return (
			<div>
				<p>Участники</p>
				{
					(members)
						? members.map(members =>
							<div className="list__participantItem" key={members.id}>
								<p>{members.name}</p>
							</div>
						)
						: null
				}
				<div className="addNewParticipants__input">
					<input ref={ref => {this.input = ref}} />
				</div>
				<div className="addNewParticipants__btn">
					<Button onClick={() => this.addMembers()} > Добавить уч. </Button>
				</div>
			</div>
		);
	}
}
export default MembersForm;
