import moment from "moment";

const initialState = {
	daysInMonth : moment().daysInMonth(),
	firstMonthDate: moment().date(1).format("DD-MM-YYYY"),
	lastMonthDate: moment().date(moment().daysInMonth()).format("DD-MM-YYYY"),
	selectDay : moment().format("DD-MM-YYYY"),
	selectTabs : moment().date(),
	daysMonth: []
};

export function month(state = initialState, action) {
	switch (action.type) {
		case "SWITCH_MONTH":
			return{
				...state,
				...action.payload
			};
		case "RESET_MONTH":
			return action.payload;
		case "SWITCH_TAB":
			return{
				...state,
				...action.payload
			};
		default:
			return state
	}
}

export function monthTasksOnDay(state = [], action) {
	switch (action.type) {
		case "TASKS_ON_MONTH":
			return action.payload;
		default:
			return state
	}
}
