import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class FormTask extends Component {
	changeField = (field, value) => {
		const { changeField } = this.props;
		const changes = {};
		changes[field] = value;
		changeField(changes);
	};

	addTask = () => {
		const {formTask, addTask} = this.props;
		addTask(formTask);
	};

	editTask = () => {
		const {formTask, editTaskFromForm} = this.props;
		editTaskFromForm(formTask);
	};

	render() {
		const { formTask, isClose } = this.props;
		return (
			<div>
				<TextField
					id="standard-name"
					label="Название задачи"
					inputProps={{ maxlength: "40"}}
					value={formTask.name}
					onChange={(e) => this.changeField('name', e.target.value)}
					margin="normal"
				/>
				<TextField
					id="standard-name"
					label="Описание задачи"
					inputProps={{ maxlength: "120"}}
					multiline
					rowsMax="4"
					value={formTask.description}
					onChange={(e) => this.changeField('description', e.target.value)}
					margin="normal"
				/>
				<TextField
					id="datetime-local"
					label="Дата начала задачи"
					type="datetime-local"
					value={formTask.dateStart}
					onChange={(e) => this.changeField('dateStart', e.target.value)}
					margin="normal"
				/>
				<TextField
					id="datetime-local"
					label="Дата окончания задачи"
					type="datetime-local"
					inputProps={{ min: formTask.dateStart}}
					value={formTask.dateEnd}
					onChange={(e) => this.changeField('dateEnd', e.target.value)}
					margin="normal"
				/>
				<TextField
					label="Важность"
					type="number"
					inputProps={{ min: "0", max: "5", step: "1" }}
					value={formTask.impotant}
					onChange={(e) => this.changeField('impotant', e.target.value)}
					margin="normal"
				/>
				<br />
				<Button
					size="small"
					variant="outlined"
					onClick={(formTask.type === 'create') ? this.addTask : this.editTask}>{(formTask.type === 'create') ? 'создать' : 'редактировать'}</Button>
				<Button
					size="small"
					variant="outlined"
					onClick={isClose}>Закрыть</Button>
			</div>
		)
	}
}

export default FormTask;
