import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';

let ContactForm = props => {
	const { handleSubmit, formTask } = props;
	return (
		<form onSubmit={handleSubmit}>
			<div>
				<label htmlFor="name">Название</label>
				<Field name="name" component="input" type="text" value='123'/>
			</div>
			<div>
				<label htmlFor="description">Описание</label>
				<Field name="description" component="input" type="text" value={formTask.description} />
			</div>
			<label htmlFor="description">Введите дату начала</label>
			<Field name="dateStart" component="input" type="date"	/>
			<label htmlFor="dateEnd">Введите дату окончания</label>
			<Field name="dateEnd" component="input" type="date"	/>
			<button type="submit">Submit</button>
		</form>
	)
};

ContactForm = reduxForm({
	// a unique name for the form
	form: 'contact'
})(ContactForm);

ContactForm = connect (
	state => ({
	formValues: {
		id: ContactForm('formName')(state, 'id')
	}
})
)(ContactForm);

export default ContactForm;
