export function testReducers(state = [], action) {
  if (action.type === "FETCH_SUCCESS") {
    return action.payload;
  }
  if (action.type === "ADD_SUCCESS") {
    console.log(action.payload);
    return action.payload;
  }
  if (action.type === "DELETE_SUCCESS") {
    return action.payload;
  }
  return state;
}

const initialStateForm = {
  name: '',
  description: ''
};
export function formReducers(state = initialStateForm, action) {
  switch (action.type) {
    case "CLEAR_FORM":
      return initialStateForm;
    case "EDIT_TASK":
      return {
        ...action.payload
      };
    case "CHANGE_FIELD":
      return {
        ...state,
        ...action.payload
      };
    default:
      return state;
  }
}
