import {getAllTasks} from "../apiService";
import moment from 'moment';

const searchTasks = (dateStart) => getAllTasks().filter( task =>
	moment(task.dateStart, "YYYY-MM-DDTHH:mm").format("DD-MM-YYYY") === dateStart
).sort(function(a, b){
	return moment(a.dateStart, "YYYY-MM-DDTHH:mm").format('X')-moment(b.dateStart, "YYYY-MM-DDTHH:mm").format('X')
});


const createDaysMonth = (selectDay) => {
	const daysInMonth = moment(selectDay, "DD-MM-YYYY").daysInMonth();
	const days = [];
	for (var i = 1; i <= daysInMonth; i++){
		days.push(moment(selectDay, "DD-MM-YYYY").date(i).format("DD-MM-YYYY"))
	}
	return days
};

export const resetMonth = () => dispatch => {
	const daysInMonth = moment().daysInMonth();
	const firstMonthDate = moment().date(1).format("DD-MM-YYYY");
	const lastMonthDate = moment().date(daysInMonth).format("DD-MM-YYYY");
	const tasksOnMonth = [];
	for (let i = 0; i < 31; i++) {
		const date = moment(firstMonthDate, "DD-MM-YYYY").add(i, 'day').format("DD-MM-YYYY");
		tasksOnMonth.push({
			date,
			tasks: searchTasks(date)})
	}
	console.log(tasksOnMonth);
	dispatch({
		type: "TASKS_ON_DAY",
		payload: tasksOnMonth
	});
	dispatch({
		type: "RESET_MONTH",
		payload: { daysInMonth, firstMonthDate, lastMonthDate }
	})
};

export const nextMonth = (selectFirst) => dispatch => {
	const daysInMonth = moment(selectFirst, "DD-MM-YYYY").add(1, "month").daysInMonth();
	const firstMonthDate = moment(selectFirst, "DD-MM-YYYY").add(1, "month").date(1).format("DD-MM-YYYY");
	const lastMonthDate = moment(selectFirst, "DD-MM-YYYY").add(1, "month").date(daysInMonth).format("DD-MM-YYYY");
	const selectTabs = (
		moment().month() ===  moment(firstMonthDate, "DD-MM-YYYY").month()
		&& moment().year() ===  moment(firstMonthDate, "DD-MM-YYYY").year()
	) ? moment().date() : 1;
	const selectDay = moment(firstMonthDate, "DD-MM-YYYY").date(+selectTabs).format("DD-MM-YYYY");
	const daysMonth = createDaysMonth(selectDay);
	const tasksOnDay = searchTasks(selectDay);
	console.log(tasksOnDay);
	dispatch({
		type: "TASKS_ON_DAY",
		payload: tasksOnDay
	})
	dispatch({
		type: "RESET_MONTH",
		payload: { daysInMonth, selectDay, selectTabs, daysMonth, firstMonthDate, lastMonthDate }
	})
}

export const changeWeek = (weekDays) => dispatch => {
	const {selectFirst, selectLast, side} = weekDays;
	let firstWeekDate;
	let lastWeekDate;
	switch (side) {
		case 'next': {
			firstWeekDate = moment(selectFirst, "DD-MM-YYYY").add(1, 'week').format("DD-MM-YYYY");
			lastWeekDate = moment(selectLast, "DD-MM-YYYY").add(1, 'week').format("DD-MM-YYYY");
			break;
		}
		case 'prev': {
			firstWeekDate = moment(selectFirst, "DD-MM-YYYY").subtract(1, 'week').format("DD-MM-YYYY");
			lastWeekDate = moment(selectLast, "DD-MM-YYYY").subtract(1, 'week').format("DD-MM-YYYY");
			break;
		}
	}
	const tasksOnWeek = [];
	for (let i = 0; i < 7; i++) {
		const date = moment(firstWeekDate, "DD-MM-YYYY").add(i, 'day').format("DD-MM-YYYY");
		tasksOnWeek.push({
			date,
			tasks: searchTasks(date)})
	}
	dispatch({
		type: "TASKS_ON_WEEK",
		payload: tasksOnWeek
	});
	dispatch({
		type: "SWITCH_WEEK",
		payload: { firstWeekDate, lastWeekDate }
	})
};

export const prevMonth = (selectFirst) => dispatch => {
	const daysInMonth = moment(selectFirst, "DD-MM-YYYY").subtract(1, "month").daysInMonth();
	const firstMonthDate = moment(selectFirst, "DD-MM-YYYY").subtract(1, "month").date(1).format("DD-MM-YYYY");
	const lastMonthDate = moment(selectFirst, "DD-MM-YYYY").subtract(1, "month").date(daysInMonth).format("DD-MM-YYYY");
	const selectTabs = (
		moment().month() ===  moment(firstMonthDate, "DD-MM-YYYY").month()
		&& moment().year() ===  moment(firstMonthDate, "DD-MM-YYYY").year()
	) ? moment().date() : 1;
	const selectDay = moment(firstMonthDate, "DD-MM-YYYY").date(+selectTabs).format("DD-MM-YYYY");
	const daysMonth = createDaysMonth(selectDay);
	const tasksOnDay = searchTasks(selectDay);
	console.log(tasksOnDay);
	dispatch({
		type: "TASKS_ON_DAY",
		payload: tasksOnDay
	})
	dispatch({
		type: "RESET_MONTH",
		payload: { daysInMonth, selectDay, selectTabs, daysMonth, firstMonthDate, lastMonthDate }
	})
}
