export const TASK_FROM_DAY = "TASK_FROM_DAY";
export const FILTER_TASKS = "FILTER_TASKS";
export const RESET_BUTTON = "RESET_BUTTON";
export const SWITCH_BUTTON = "SWITCH_BUTTON";

export function listTaskReducers(state = [], action) {
  switch (action.type) {
    case TASK_FROM_DAY:
      return action.payload;
    case FILTER_TASKS:
      return action.payload;
    default:
      return state
  }
}

const initialStateSelectedButton = {
  selectTabs : "today",
};

export function selectedButton(state = initialStateSelectedButton, action) {
  switch (action.type) {
    case RESET_BUTTON:
      return action.payload;
    case SWITCH_BUTTON:
      return{
        ...state,
        ...action.payload
      };
    default:
      return state
  }
}
