import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Badge from '@material-ui/core/Badge';
import MailIcon from '@material-ui/icons/Mail';

import moment from 'moment';
import FormTask from '../containers/FormTaskPage';

import './css/ListTask.css'

class ListTasks extends Component {
  componentDidMount() {
    const { searchAllTasks } = this.props;
    searchAllTasks();
  }

  editTask = (id) => {
    const { editTask } = this.props;
    editTask(id);
  };

  deleteTask = (id) => {
    const { delTask } = this.props;
    delTask(id);
  };

  render() {
    const {
      tasks, formTask, addTask, sendEditTask,
      searchAllTasks, searchTodayTasks, searchTomorrowTasks,
      searchWeekTasks, searchMonthTasks, cleanForm, selectedButton
    } = this.props;
    return (
      <div>
        <Paper>
        <div>
          <FormTask
            sendEditTask={sendEditTask}
            addTask={addTask}
            formTask={formTask}
          />
        </div>
          <div className="buttonGroup">
            <div>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                color={selectedButton === 'all' ? "primary" : "secondary"}
                onClick={searchAllTasks}
              >
                Все
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                color={selectedButton === 'today' ? "primary" : "secondary"}
                onClick={searchTodayTasks}
              >
                Сегодня
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                color={selectedButton === 'tomorrow' ? "primary" : "secondary"}
                onClick={searchTomorrowTasks}
              >
                Завтра
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                color={selectedButton === 'week' ? "primary" : "secondary"}
                onClick={searchWeekTasks}
              >
                Неделя
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                color={selectedButton === 'month' ? "primary" : ""}
                onClick={searchMonthTasks}
              >
                Месяц
              </Button>
            </div>
            <div>
              <Button
                variant="contained"
                size="small"
                color="primary"
                onClick={cleanForm}
              >
                Создать задачу
              </Button>
            </div>
          </div>
          <Divider/>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell className="tableHeadName" align="center">
                  Название
                </TableCell>
                <TableCell className="tableHeadDescription" align="center">
                  Описание
                </TableCell>
                <TableCell className="tableHeadDate" align="center">
                  Дата начала
                </TableCell>
                <TableCell className="tableHeadDate" align="center">
                  Дата окончания
                </TableCell>
                <TableCell className="tableHeadDate" align="center">
                  Важность
                </TableCell>
                <TableCell></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tasks.map(t =>
                <TableRow key={t.id}>
                  <TableCell align="center">{t.name}</TableCell>
                  <TableCell align="center">{t.description}</TableCell>
                  <TableCell align="center">{moment(t.dateStart).format('DD.MM.YYYY HH:mm')}</TableCell>
                  <TableCell align="center">{moment(t.dateEnd).format('DD.MM.YYYY HH:mm')}</TableCell>
                  <TableCell align="center">
                    <Badge color="secondary" badgeContent={t.important}>
                      <MailIcon />
                    </Badge></TableCell>
                  <TableCell align="center">
                    <Button
                      size="small"
                      color="primary"
                      onClick={() => this.editTask(t.id)}
                    >
                      Редактировать
                    </Button>
                    <Button
                      size="small"
                      color="secondary"
                      onClick={() => this.deleteTask(t.id)}
                    >
                      Удалить
                    </Button>
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </Paper>
      </div>
    );
  }
}

export default ListTasks;
