import { getAllTasks, deleteTask, postTask, putTask } from "../apiService";

export const getSt = (body) => dispatch => {
  const items = getAllTasks();
  dispatch({ type: "FETCH_SUCCESS", payload: items })
};

export const addTask = (body) => dispatch => {
  const cleanBody = {...body};
  delete cleanBody.type;
  postTask(cleanBody);
  const items = getAllTasks();
  dispatch({ type: "ADD_SUCCESS", payload: items });
  dispatch({ type: "CLEAR_FORM" })
};

export const delTask = (id) => dispatch => {
  deleteTask(id);
  const items = getAllTasks();
  dispatch({ type: "DELETE_SUCCESS", payload: items })
};

export const editTask = (id) => dispatch => {
  const items = getAllTasks().filter(t => t.id === id)[0];
  console.log(items);
  dispatch({ type: "EDIT_TASK", payload: items })
};

export const changeField = (field) => dispatch => {
  dispatch({ type: "CHANGE_FIELD", payload: field})
};

export const sendEditTask = (body) => dispatch => {
  const cleanBody = {...body};
  delete cleanBody.type;
  putTask(cleanBody);
  const items = getAllTasks();
  dispatch({ type: "ADD_SUCCESS", payload: items });
};
