import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Drawer from '@material-ui/core/Drawer';

import moment from 'moment';
import FormTask from '../containers/FormTaskPage';

import './css/Calendar.css'

class CalendarWeek extends Component {
  state = {
    top: false
  };

  componentDidMount(){
    const { dumpWeek } = this.props;
    dumpWeek();
  }

  editTask = (id) => {
    const { editTask } = this.props;
    editTask(id);
    this.toggleDrawer('top', true)
  };

  toggleDrawer = (side, open) => () => {
    this.setState({
      [side]: open,
    });
    const { cleanForm } = this.props;
    cleanForm();
  };

  render() {
    const {
      dumpWeek, changeWeek, week, tasks, sendEditTask,
      addTask, formTask
    } = this.props;
    return (
      <div>
        <Paper className="calendarContainer">
          <p className="calendarTitle">Календарь на неделю</p>
          <p>Неделя с {week.firstWeekDate} по {week.lastWeekDate}</p>
          <div>
            <Drawer
              anchor="top"
              open={this.state.top}
              onClose={this.toggleDrawer('top', false)}
            >
              <FormTask
                isClose={this.toggleDrawer('top', false)}
                sendEditTask={sendEditTask}
                addTask={addTask}
                formTask={formTask}
              />
            </Drawer>
          </div>
          <div className="buttonGroup">
            <div>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'prev'
              }
              )}
              >
                Предыдущая неделя
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={dumpWeek}>Текущая неделя</Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'next'
              }
              )}
              >
                Следующая неделя
              </Button>
            </div>
            <div>
              <Button
                variant="contained"
                size="small"
                color="primary"
                onClick={this.toggleDrawer('top', true)}
              >
                Создать задачу
              </Button>
            </div>
          </div>
          {tasks.map(d =>
            <ExpansionPanel
              key={d.date}
              disabled={!d.tasks.length}
            >
              <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                <p>
                  {d.date}
                  <p>На данный день запланировано {d.tasks.length} задания(ий)</p>
                </p>
              </ExpansionPanelSummary>
              <ExpansionPanelDetails>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell align="right">Название</TableCell>
                      <TableCell align="right">Описание</TableCell>
                      <TableCell align="right">Дата начала</TableCell>
                      <TableCell align="right">Дата окончания</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {d.tasks.map(t =>
                      <TableRow key={t.id}>
                        <TableCell align="center">{t.name}</TableCell>
                        <TableCell align="center">{t.description}</TableCell>
                        <TableCell align="center">{moment(t.dateStart).format('DD.MM.YYYY HH:mm')}</TableCell>
                        <TableCell align="center">{moment(t.dateEnd).format('DD.MM.YYYY HH:mm')}</TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </ExpansionPanelDetails>
            </ExpansionPanel>
          )}
        </Paper>
      </div>
    );
  }
}

export default CalendarWeek;
