import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import FormTask from '../additionalComponent/FormTask';
import moment from 'moment';

import './css/FirstPage.css'

class CalendarWeek extends Component {
  componentDidMount() {
    this.fetchData();
  }
  fetchData = () => {
    const { getSt } = this.props;
    getSt();
  };
  newTask = () => {
    const { clearForm } = this.props;
    clearForm()
  };
  editTask = (id) => {
    const { editTask } = this.props;
    editTask(id);
  };
  deleteTask = (id) => {
    const { delTask } = this.props;
    delTask(id);
  };
  render() {
    const { testReducers, formTask, changeField, addTask, sendEditTask } = this.props;
    return (
      <div>
        <Paper>
          <button onClick={this.newTask}>Новая таска</button>
        <div>
          <FormTask sendEditTask={sendEditTask} addTask={addTask} formTask={formTask} changeField={changeField}/>
        </div>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell align="right">test</TableCell>
                <TableCell align="right">name</TableCell>
                <TableCell align="right">description</TableCell>
                <TableCell align="right">dateStart</TableCell>
                <TableCell align="right">dateEnd</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {testReducers.map(t =>
                <TableRow key={t.id}>
                  <TableCell align="right">{t.id}</TableCell>
                  <TableCell align="center">{t.name}</TableCell>
                  <TableCell align="center">{t.description}</TableCell>
                  <TableCell align="center">{moment(t.dateStart).format('YYYY-MM-DD HH:mm')}</TableCell>
                  <TableCell align="center">{moment(t.dateEnd).format('YYYY-MM-DD HH:mm')}</TableCell>
                  <TableCell align="center">
                    <button onClick={() => this.editTask(t.id)}> red </button>
                    <button onClick={() => this.deleteTask(t.id)}> удалить </button>
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </Paper>
      </div>
    );
  }
}

export default CalendarWeek;
