import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import './css/FirstPage.css'
import moment from "./ListTasks";

class CalendarWeek extends Component {
  componentDidMount(){
    const { resetWeek } = this.props;
    resetWeek();
  }
  render() {
    const {
      resetWeek, nextWeek, prevWeek, week, tasks
    } = this.props;
    return (
      <div>
        <Paper>
          <p>Календарь</p><p>Неделя с {week.firstWeekDate} по {week.lastWeekDate}</p>
          <br/>
          <button onClick={() => prevWeek({
            selectFirst: week.firstWeekDate,
            selectLast: week.lastWeekDate
          }
          )}
          >
            Предыдущая неделя
          </button>
          <button onClick={resetWeek}>Текущая неделя</button>
          <button onClick={() => nextWeek({
            selectFirst: week.firstWeekDate,
            selectLast: week.lastWeekDate
          }
          )}
          >
            Следующая неделя
          </button>
          {tasks.map(d =>
            <ExpansionPanel>
              <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                <p>
                  {d.date}
                </p>
              </ExpansionPanelSummary>
              <ExpansionPanelDetails>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Second PPPPPPAGE</TableCell>
                      <TableCell align="right">Calories</TableCell>
                      <TableCell align="right">Fat</TableCell>
                      <TableCell align="right">Carbs</TableCell>
                      <TableCell align="right">Protein</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {d.tasks.map(t =>
                      <TableRow key={t.id}>
                        <TableCell align="center">{t.name}</TableCell>
                        <TableCell align="center">{t.description}</TableCell>
                        <TableCell align="center">{moment(t.dateStart).format('DD.MM.YYYY HH:mm')}</TableCell>
                        <TableCell align="center">{moment(t.dateEnd).format('DD.MM.YYYY HH:mm')}</TableCell>
                        <TableCell align="center">
                          <button onClick={() => this.editTask(t.id)}> red </button>
                          <button onClick={() => this.deleteTask(t.id)}> удалить </button>
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </ExpansionPanelDetails>
            </ExpansionPanel>
          )}
        </Paper>
      </div>
    );
  }
}

export default CalendarWeek;
