import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';

import FormTask from '../containers/FormTaskPage';
import Tasks from './Tasks';

import './css/Calendar.css'

class CalendarWeek extends Component {
  componentDidMount(){
    const { dumpWeek } = this.props;
    dumpWeek();
  }

  editTask = (id) => {
    const { editTask } = this.props;
    editTask(id);
  };

  deleteTask = (id) => {
    const { delTask } = this.props;
    delTask(id);
  };

  render() {
    const {
      dumpWeek, changeWeek, week, tasks, sendEditTask,
      addTask, formTask, cleanForm
    } = this.props;
    return (
      <div>
        <Paper className="calendarContainer">
          <p className="calendarTitle">Календарь на неделю</p>
          <p>Неделя с {week.firstWeekDate} по {week.lastWeekDate}</p>
          <div>
            <FormTask
              sendEditTask={sendEditTask}
              addTask={addTask}
              formTask={formTask}
            />
          </div>
          <div className="buttonGroup">
            <div>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'prev'
              }
              )}
              >
                Предыдущая неделя
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={dumpWeek}>Текущая неделя</Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'next'
              }
              )}
              >
                Следующая неделя
              </Button>
            </div>
            <div>
              <Button
                variant="contained"
                size="small"
                color="primary"
                onClick={cleanForm}
              >
                Создать задачу
              </Button>
            </div>
          </div>
          <Tasks {...this.props} />
        </Paper>
      </div>
    );
  }
}

export default CalendarWeek;
