import React from 'react';
import uuid from 'uuid/v4';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import DeleteIcon from '@material-ui/icons/Delete';

import './css/FormTask.css'

class MembersForm extends React.PureComponent {
	addMembers = () => {
		const { members, updateMembers } = this.props;
		console.log(this.input.value);
		let editMembers = (members) ? [...members] : [];
		if (this.input.value) {
			editMembers.push({
				id: uuid(),
				name: this.input.value
			})
		}
		updateMembers(editMembers);
		this.input.value = null
	};

	deleteMembers = (id) => {
		const { members, updateMembers } = this.props;
		const editParticipants = members.filter(members => members.id !== id);
		updateMembers(editParticipants);
	};

	render() {
		const { members } = this.props;
		return (
			<div>
				<div>
					<p>Участники</p>
					<TextField
						label="Имя участника"
						inputProps={{ maxLength: "15"}}
						inputRef={ref => {this.input = ref}} />
				</div>
				<div>
					<Button
						size="small"
						variant="contained"
						onClick={() => this.addMembers()} > Добавить </Button>
				</div>
				{
					(members)
						? members.map(members =>
							<div className="members" key={members.id}>
								<p>{members.name}</p>
								<Button className="roundedButton" onClick={() => this.deleteMembers(members.id)}><DeleteIcon /></Button>
							</div>
						)
						: null
				}
			</div>
		);
	}
}
export default MembersForm;
