import React from 'react';
import uuid from 'uuid/v4';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class MembersForm extends React.PureComponent {
	deleteMember = (id) => {
		const { members, updateParticipants } = this.props;
		const editParticipants = members.filter(members => members.id !== id);
		updateParticipants(editParticipants);
	};
	addParticipants = () => {
		const { members, updateParticipants } = this.props;
		const editParticipants = (members) ? [...members] : [];
		if (this.input.state.value) {
			editParticipants.push({
				id: uuid(),
				name: this.input.state.value
			})
		}
		updateParticipants(editParticipants);
		this.input.state.value = null
	};
	render() {
		const { members } = this.props;
		return (
			<div>
				<div>
					<h4>Участники: { (members && members.length < 1) ? "Нет" : null } </h4>
					{
						(members)
							? members.map(members =>
								<div key={members.id}>
									<p>{members.name}</p>
									<Button type="danger" onClick={() => this.deleteMember(members.id)}>Удалить</Button>
								</div>
							)
							: null
					}
				</div>
				<div>
					<div>
						<TextField ref={ref => {this.input = ref}} />
					</div>
					<div>
						<Button onClick={() => this.addParticipants()} > Добавить уч. </Button>
					</div>
				</div>
			</div>
		);
	}
}
export default MembersForm;
