import React, { Component } from 'react';
import Paper from '@material-ui/core/Paper';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Badge from '@material-ui/core/Badge';
import MailIcon from '@material-ui/icons/Mail';

import moment from 'moment';
import FormTask from '../containers/FormTaskPage';

import './css/Calendar.css'

class CalendarWeek extends Component {
  componentDidMount(){
    const { dumpWeek } = this.props;
    dumpWeek();
  }

  render() {
    const {
      dumpWeek, changeWeek, week, tasks, sendEditTask,
      addTask, formTask, cleanForm, editTask, delTask
    } = this.props;
    return (
      <div>
        <Paper className="calendarContainer">
          <p className="calendarTitle">Календарь на неделю</p>
          <p>Неделя с {week.firstWeekDate} по {week.lastWeekDate}</p>
          <div>
            <FormTask
              sendEditTask={sendEditTask}
              addTask={addTask}
              formTask={formTask}
            />
          </div>
          <div className="buttonGroup">
            <div>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'prev'
              }
              )}
              >
                Предыдущая неделя
              </Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={dumpWeek}>Текущая неделя</Button>
              <Button
                className="buttonGroup-button"
                size="small"
                variant="outlined"
                onClick={() => changeWeek({
                selectFirst: week.firstWeekDate,
                selectLast: week.lastWeekDate,
                side: 'next'
              }
              )}
              >
                Следующая неделя
              </Button>
            </div>
            <div>
              <Button
                variant="contained"
                size="small"
                color="primary"
                onClick={() => cleanForm()}
              >
                Создать задачу
              </Button>
            </div>
          </div>
          {tasks.map(d =>
            <ExpansionPanel
              key={d.date}
              disabled={!d.tasks.length}
            >
              <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                <div className="expansionPanelSummaryContainer">
                  <p className="expansionPanelSummaryTitle"><strong>{d.date}</strong>
                  </p>
                  <p className="expansionPanelSummarySubtitle">На этот день запланировано {d.tasks.length} задания(ий)</p>
                </div>
              </ExpansionPanelSummary>
              <ExpansionPanelDetails>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell align="right">Название</TableCell>
                      <TableCell align="right">Описание</TableCell>
                      <TableCell align="right">Дата начала</TableCell>
                      <TableCell align="right">Дата окончания</TableCell>
                      <TableCell className="tableHeadDate" align="center">
                        Важность
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {d.tasks.map(t =>
                      <TableRow key={t.id}>
                        <TableCell align="center">{t.name}</TableCell>
                        <TableCell align="center">{t.description}</TableCell>
                        <TableCell align="center">{moment(t.dateStart).format('DD.MM.YYYY HH:mm')}</TableCell>
                        <TableCell align="center">{moment(t.dateEnd).format('DD.MM.YYYY HH:mm')}</TableCell>
                        <TableCell align="center">
                          <Badge color="secondary" badgeContent={t.important}>
                            <MailIcon />
                          </Badge>
                        </TableCell>
                        <TableCell align="center">
                          <Button
                            size="small"
                            color="primary"
                            onClick={() => editTask(t.id)}
                          >
                            Редактировать
                          </Button>
                          <Button
                            size="small"
                            color="secondary"
                            onClick={() => delTask(t.id)}
                          >
                            Удалить
                          </Button>
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </ExpansionPanelDetails>
            </ExpansionPanel>
          )}
        </Paper>
      </div>
    );
  }
}

export default CalendarWeek;
